import React from 'react';
import CSSTransitionGroup from 'react-transition-group';

const Contact = (props) => {
  // "name": "Jeanne Locker",
  //     "label": "Software Engineer",
  //     "email": "jealocker@gmail.com",
  //     "phone": "(917) 692-8407",
  //     "website": "linkedin.com",
  //     "summary": "Aspiring pro beep-booper",
  //     "location": {
  //       "postalCode": "10024",
  //       "city": "New York",
  //       "countryCode": "US",
  //       "region": "NY"
  //     },
  //     "pdf": "../../../../jobs/locker jeanne resume.pdf"
  const { name, label, email, phone, website, summary, location, pdf } = props.info;
  const layout = {display: 'flex', flexDirection: 'column', alignItems: 'center'}
  return (
      <div className="contact__container" style={{display: 'flex'}}>
        <div className="contact__modal">
          <CSSTransitionGroup 
            style={layout}
            component="div"
            transitionName="slide"
            transitionEnterTimeout={5000}
            transitionLeaveTimeout={3000}
          >
            <h1 className="contact__header" >Contact</h1>

            <h3>
              <a
                href="mailto:skok@vova.io?subject=Resume%20Inquiry"
                style={{textDecoration: 'none'}}
              >
                {email}
              </a>
            </h3>

            <h3>
              <a
                href="https://linkedin.com/in/iamskok"
                style={{ textDecoration: 'none'}}
                target="_blank"
                rel="noopener noreferrer"
              >
                {website}
              </a>
            </h3>
          </CSSTransitionGroup>
        </div>
      </div>
    )
}

export default Contact;
